// Ensure that the contents of the message body matches the original message
// looks up the original message submitted and uses XmlUnit.Diff.similar() between
// it and the transmitted message

import org.custommonkey.xmlunit.Diff;

groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )

def skipTextNodes(node) {
	while ( node.getNodeType() != org.w3c.dom.Node.ELEMENT_NODE ) {
		node = node.getNextSibling()	
	}	
	return node
}

def getOriginalMsg(name) {
	// find the original message body
	def msgRequest = groovyUtils.getXmlHolder( name + "#Request" )
	msgRequest.namespaces["soap"] = "http://schemas.xmlsoap.org/soap/envelope/"

	def msgBody = msgRequest.getDomNode("//soap:Body")
	return groovyUtils.getXml(skipTextNodes(msgBody.getFirstChild()))
}


// grab message body string from message
response = groovyUtils.getXmlHolder( "GetMessages#Response" )
response.namespaces["mb"] = "urn:riv:infrastructure:itintegration:messagebox:1"
response.namespaces["get"] = "urn:riv:infrastructure:itintegration:messagebox:GetMessagesResponder:1"
response.namespaces["reg"] = "urn:riv:itintegration:registry:1"

def tkP = "urn:riv:itintegration:engagementindex:ProcessNotificationResponder:1"
def tkA = "urn:riv:insuranceprocess:healthreporting:ReceiveMedicalCertificateAnswerResponder:1"

def verify(testName, org, tk) {
	def nodes = response.getNodeValues(
		"//reg:ServiceContractNamespace[text() = \"" + tk + "\"]" +
		"/../../get:targetOrganization[text() = \"" + org + "\"]/../get:message")
	assert nodes.length == 1	
	def msg = nodes[0]

	def orig = getOriginalMsg(testName)

	assert new Diff(msg, orig).similar()

}

verify("TP0: SubmitM1(PN-MT-test-1)", "MT-test-1", tkP)
verify("SubmitM2(A-MT-test-1)", "MT-test-1", tkA)
verify("SubmitM3(PN-MT-test-2)", "MT-test-2", tkP)
verify("SubmitM4(A-MT-test-2)", "MT-test-2", tkA)

// return "Ok"

